$(document).ready(function(){var b=$("form");$("#successSubscribe").hide();$("#errorSubscribe").hide();0<b.length&&$("#btnSubmit").bind("click",function(a){a&&a.preventDefault();$("#successSubscribe").hide();$("#errorSubscribe").hide();register(b)})});
function register(b){$.ajax({type:b.attr("method"),url:b.attr("action"),data:b.serialize(),cache:!1,dataType:"json",contentType:"application/json; charset=utf-8",error:function(a){console.log("Could not connect to the registration server. Please try again later.");console.log(a)},success:function(a){console.log(a.msg);"success"!=a.result?($("#errorSubscribe").html(a.msg),$("#errorSubscribe").slideDown(a.msg)):($("#successSubscribe").html(a.msg),$("#successSubscribe").slideDown(a.msg))}})};